import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FoodDetail.css';
import CommentsComponent from './Comment';
import { useParams } from 'react-router-dom';

const FoodDetail = () => {
    const { mealId } = useParams();
    const [meal, setMeal] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Hàm gọi API để lấy dữ liệu món ăn từ cơ sở dữ liệu
        const fetchMeal = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/api/meal/getMealById/${mealId}`);
                setMeal(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching meal data:', error);
                setLoading(false);
            }
        };
        
        fetchMeal();
    }, [mealId]);

    const handleAddToCart = () => {
        alert(`Added ${quantity} of ${meal.name} to cart`);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!meal) {
        return <div>Meal not found</div>;
    }

    return (
        <div className="product-detail-container">
            <div className="product-detail">
                <div className="product-image">
                    <img src={`data:image/png;base64,${meal.image}`} alt={meal.name} />
                </div>
                <div className="product-info">
                    <h2 className="product-category">{meal.mealType.typeName}</h2>
                    <h1 className="product-name">{meal.name}</h1>
                    <p className="product-description">{meal.description}</p>
                    <div className="product-price">Price: ${meal.price.toFixed(2)}</div>
                    <div className="product-actions">
                        <label>Quantity:</label>
                        <input
                            type="number"
                            value={quantity}
                            min="1"
                            onChange={(e) => setQuantity(Number(e.target.value))}
                        />
                        <button className="add-to-cart-btn" onClick={handleAddToCart}>
                            Add to Cart
                        </button>
                    </div>
                </div>
            </div>
            {/* Comments section positioned below the product detail */}
            <CommentsComponent />
        </div>
    );
};

export default FoodDetail;
