import React, { useState } from 'react';
import './FoodDetail.css';
import CommentsComponent from './Comment';

const FoodDetail = () => {
    const [quantity, setQuantity] = useState(1);

    // Hardcoded product data for demonstration purposes
    const product = {
        name: 'Margherita Pizza',
        category: 'Pizza',
        imageUrl: '/images/margherita.jpg', // Ensure this image exists in the public/images folder
        description: 'A classic Margherita pizza with fresh tomatoes, mozzarella cheese, and basil.',
        price: 12.99,
    };

    const handleAddToCart = () => {
        alert(`Added ${quantity} of ${product.name} to cart`);
    };

    return (
        <div className="product-detail-container">
            <div className="product-detail">
                <div className="product-image">
                    <img src={product.imageUrl} alt={product.name} />
                </div>
                <div className="product-info">
                    <h2 className="product-category">{product.category}</h2>
                    <h1 className="product-name">{product.name}</h1>
                    <p className="product-description">{product.description}</p>
                    <div className="product-price">Price: ${product.price.toFixed(2)}</div>
                    <div className="product-actions">
                        <label>Quantity:</label>
                        <input
                            type="number"
                            value={quantity}
                            min="1"
                            onChange={(e) => setQuantity(e.target.value)}
                        />
                        <button className="add-to-cart-btn" onClick={handleAddToCart}>
                            Add to Cart
                        </button>
                    </div>
                </div>
            </div>
            {/* Comments section positioned below the product detail */}
            <CommentsComponent />
        </div>
    );
};

export default FoodDetail;
