// Comment.js
import React, { useState, useEffect } from 'react';
import CommentService from '../../services/CommentService';
import './Comment.css';

const Comment = ({ mealId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const currentUser = sessionStorage.getItem('username'); 

    useEffect(() => {
        // Fetch comments based on mealId
        CommentService.getComments(mealId)
            .then(response => {
                console.log("Data received from API:", response.data); // Log received data
                setComments(response.data);
            })
            .catch(error => console.error("Error fetching comments:", error));
    }, [mealId]);

    const handleCommentSubmit = (e) => {
        e.preventDefault();
        if (newComment.trim() !== '') {
            const newCommentObject = {
                id: null, 
                author: currentUser,
                content: newComment,
                created_at: new Date().toISOString(), 
                mealId: mealId,
            };

            console.log(newCommentObject)
    
            CommentService.addComment(newCommentObject)
                .then(response => {
                    setComments([...comments, response.data]);
                    setNewComment('');
                })
                .catch(error => console.error("Error adding comment:", error));
        }
    };

    return (
        <div className="comments-section">
            <h3>Comments</h3>
            <form onSubmit={handleCommentSubmit}>
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Add a comment"
                />
                <button type="submit">Submit</button>
            </form>
            <ul className="comments-list">
                {comments.map((comment, index) => (
                    <li key={index}>
                        <strong>{comment.author}</strong> - <em>
                            {comment.created_at || "Unknown date"}
                        </em>
                        <p>{comment.content}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Comment;
