import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import MealService from '../../services/MealService';

const EditMealComponent = ({ meal, onUpdateSuccess }) => {
    const [mealName, setMealName] = useState(meal.name || '');
    const [description, setDescription] = useState(meal.description || '');
    const [price, setPrice] = useState(meal.price || 0);
    const [selectedFile, setSelectedFile] = useState(null);

    useEffect(() => {
        setMealName(meal.name || '');
        setDescription(meal.description || '');
        setPrice(meal.price || 0);
    }, [meal]);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('meal', JSON.stringify({ ...meal, name: mealName, description, price }));

        if (selectedFile) {
            formData.append('image', selectedFile);
        }

        try {
            const response = await MealService.updateMeal(formData);
            if (response.data === 'success') {
                alert('Meal updated successfully!');
                if (onUpdateSuccess) onUpdateSuccess();
            } else {
                alert('Failed to update meal. Please try again.');
            }
        } catch (error) {
            console.error('Error updating meal:', error);
            alert('An error occurred while updating the meal.');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className='form-group mb-2'>
                    <label>Meal Name:</label>
                    <input
                        type="text"
                        placeholder="Enter meal name"
                        value={mealName}
                        onChange={(e) => setMealName(e.target.value)}
                        className="form-control"
                    />
                </div>

                <div className='form-group mb-2'>
                    <label>Description:</label>
                    <input
                        type="text"
                        placeholder="Enter description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        className="form-control"
                    />
                </div>

                <div className='form-group mb-2'>
                    <label>Price:</label>
                    <input
                        type="number"
                        placeholder="Enter price"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        className="form-control"
                    />
                </div>

                <div className='form-group mb-2'>
                    <label>Upload New Image:</label>
                    <input
                        type="file"
                        onChange={handleFileChange}
                        className="form-control"
                    />
                </div>

                <Button type="submit" className="btn btn-primary">
                    Save Changes
                </Button>
            </form>
        </div>
    );
};

export default EditMealComponent;
