// Comment.js
import React, { useState, useEffect } from 'react';
import CommentService from '../../services/CommentService';
import './Comment.css';

const Comment = ({ mealId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const currentUser = 'Người Dùng Hiện Tại';

    useEffect(() => {
        // Lấy bình luận dựa trên mealId
        CommentService.getComments(mealId)
            .then(response => {
                console.log("Dữ liệu nhận được từ API:", response.data); // Log dữ liệu nhận được
                setComments(response.data);
            })
            .catch(error => console.error("Lỗi khi lấy bình luận:", error));
    }, [mealId]);

    const handleCommentSubmit = (e) => {
        e.preventDefault();
        if (newComment.trim() !== '') {
            const newCommentObject = {
                author: currentUser,
                content: newComment,
                timestamp: new Date().toISOString(), // Gửi thời gian hiện tại
            };

            CommentService.addComment(mealId, newCommentObject)
                .then(response => {
                    setComments([...comments, response.data]);
                    setNewComment('');
                })
                .catch(error => console.error("Lỗi khi thêm bình luận:", error));
        }
    };

    return (
        <div className="comments-section">
            <h3>Bình Luận</h3>
            <form onSubmit={handleCommentSubmit} className="comment-form">
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Thêm bình luận..."
                    className="comment-textarea"
                    required
                />
                <button type="submit" className="submit-comment-btn">Gửi</button>
            </form>
            <ul className="comments-list">
                {comments.map((comment, index) => (
                    <li key={index} className="comment-item">
                        <strong>{comment.author}</strong> - 
                        <em>{comment.timestamp ? new Date(comment.timestamp).toLocaleString() : "Không xác định"}</em>
                        <p>{comment.content}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Comment;
